        public static int SolvePartA(string input)
        {
            var floor = 0;
            foreach (var c in input.ToCharArray())
            {
                if (c == '(') floor++;
                if (c == ')') floor--;
            }

            return floor;
        }

        public static int SolvePartB(string input)
        {
            var partBFloor = 0;
            for (var position = 1; position <= input.Length; position++)
            {
                var c = input[position - 1];
                if (c == '(') partBFloor++;
                if (c == ')') partBFloor--;

                if (partBFloor < 0) return position;
            }

            throw new Exception("Never went into the basement.");
        }
