void SolvePart1(string input, int? expectedAnswer = null)
{
    var distinctCharacters = 4;
    Solve(input, distinctCharacters, expectedAnswer);
}
void SolvePart2(string input, int? expectedAnswer = null)
{
    var distinctCharacters = 14;
    Solve(input, distinctCharacters, expectedAnswer);
}

static void Solve(string input, int distinctCharacters, int? expectedAnswer)
{
    var chars = input.ToCharArray();
    int answer = -1;
    for (int i = distinctCharacters-1; i < chars.Length; i++)
    {
        var lastCharacters = new List<char>();
        for (int j = i - distinctCharacters + 1; j <= i; j++)
        {
            lastCharacters.Add(chars[j]);
        }
        var groups = lastCharacters.GroupBy(x => x);
        if (groups.Count() == distinctCharacters)
        {
            answer = i + 1;
            break;
        }
    }

    string expectedAnswerString = expectedAnswer != null ? $" expected answer: {expectedAnswer.Value}" : "";
    Console.WriteLine($"From input: {input.Substring(0, 5)} - answer: {answer} {expectedAnswerString}");
}